const mongoose = require('mongoose')

//sheindy hirshman -for activity-log
const LogsSchema = mongoose.Schema({
    logDate: { type: Date, required: true },
    userName: { type: String, required: true },
    appName: { type: String, required: true },
    action: { type: String, required: true },
    details: { type: String, required: true },
    userAgent: { type: String, required: false },
    host: { type: String, required: false },
    actionBody: { type: String, required: false }
})

LogsSchema.pre('save', function (next) {
    const log = this;
    log.appName = log.appName.replace(/^./, log.appName[0].toUpperCase());
    console.log(log.appName);
    next()

})

module.exports = mongoose.model('activityLogs', LogsSchema)