const mongoose = require('mongoose')
const calendarSettingsSchema = mongoose.Schema({
    
    workingDaysAndHours:{ 
        type: [Object],
        default : [
            { day: 6, name: "Sunday", letter: "S", startTime: "08:00", endTime: "16:00" },
            { day: 0, name: "Monday", letter: "M", startTime: "08:00", endTime: "16:00" },
            { day: 1, name: "Tuesday", letter: "T", startTime: "08:00", endTime: "16:00" },
            { day: 2, name: "Wednesday", letter: "W", startTime: "08:00", endTime: "16:00" },
            { day: 3, name: "Thursday", letter: "T", startTime: "08:00", endTime: "16:00" }
        ],
        required: true
    },
    formatDate: { 
        type: String,
        default : 'dd/MM/yyyy',
        required: true
    },
    meetingsDurations:{
        type: Array,
        default : [15,30,45],
        required: true
    },
    isAbleNotification:{
        type: Boolean,
        default : true,
        required: true
    }
})

module.exports = mongoose.model('CalendarSettings', calendarSettingsSchema)
