
const mongoose = require('mongoose')
const hangoutSchema = mongoose.Schema({

    name: String,
    //owner
    owner: {
        type: String,
        //  ref: 'User'
    },
    //managers
    managers: [{
        type: mongoose.Schema.Types.ObjectId,
        ref: 'User'
    }],
    //members
    members: [{
        type: mongoose.Schema.Types.ObjectId,
        ref: 'User'
    }],
    //     roomID:{
    //     type: mongoose.Schema.Types.ObjectId,
    //     ref:'Conversation'
    // },
    conversations: [{
        type: mongoose.Schema.Types.ObjectId,
        ref: 'Conversation'
    }],
    profileGroup: String,
    deleted: { type: Boolean, default: false },
    deleteDate: Date,
    link: String,
    superGroup: Boolean,
    // isMute: { type: Boolean, default: false }
})

module.exports = mongoose.model('Hangout', hangoutSchema)

