const mongoose = require('mongoose');

const LmsReviewSchema = mongoose.Schema({
    courseId: { type: mongoose.Schema.Types.ObjectId, ref: 'Course'},
    username: { type: String },
    photoURL: { type: String },
    content: { type: String },
    date: { type: String },
    stars: { type: Number }
})

module.exports = mongoose.model('LmsReview', LmsReviewSchema);