//mailer
const mongoose = require('mongoose');

const templateSchema = mongoose.Schema({
   id:String,
   name:String,
   createUser:{ type: mongoose.Schema.Types.ObjectId, ref: 'User' },
   createDate:String,
   modifyDate:String,
   status:String,
   html:String,
   ref:String,
   generalSettings:{},
   objects:{},
   signature:{}
    
})

module.exports = mongoose.model('MailTemplate', templateSchema)
