//צוות calendar
const mongoose = require('mongoose')
const notificationSchema = mongoose.Schema({   
    date: { 
        type: Date
    },
    body: { 
        type: Object
    },
    type:{
        type: String
    },
    time:{
        type: String
    },
    num:{
        type: Number
    },
    id:{ type: String}
    // ,
    // eventId:{
    //     type:mongoose.Schema.Types.ObjectId,
    //     ref:'Event'     
    // }
})

module.exports = mongoose.model('NotificationCalendar', notificationSchema)