const mongoose = require("mongoose");
const bcrypt = require('bcrypt');

//Tzofiya Cohen - for Buisness

const ProductForBuisness = mongoose.Schema({
    buisness: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'Buisness'
    },
    name: {
        type: String,
        require: true
    },
    description: {
        type: String
    },
    amount: {
        type: Number,
        default: 1
    },
    price: {
        type: Number,
    },
    images: {
        type: String
    },
    codeSKU: {
        type: Number,
    },
    discount: {
        type: Number
    }
})
module.exports = mongoose.model('ProductForBuisness', ProductForBuisness)




