const mongoose = require('mongoose')

//Scheme full of objects
const sharedObjectsSchema = mongoose.Schema({
    sharingUser: { type: mongoose.Schema.Types.ObjectId, ref: 'User' },
    user: { type: mongoose.Schema.Types.ObjectId, ref: 'User' },
    contact: { type: mongoose.Schema.Types.ObjectId, ref: 'Contact' },
    objectId: { type: String },
    schemaName: { type: String },
    permission: { type: mongoose.Schema.Types.ObjectId, ref: 'UserPermission' },
    fromTeam: { type: mongoose.Schema.Types.ObjectId, ref: 'Team' },
})


module.exports = mongoose.model("SharedObject", sharedObjectsSchema)