const mongoose = require('mongoose')

const systemWaveSchema = mongoose.Schema({
    timestamp: { type: Date, default: Date.now },
    subject: {type:String},
    body: {type: String},
    files: [String],
    to:[{type:String}],
    from:{type:String},
    source:{type:String},
    user:[{
        type: mongoose.Schema.Types.ObjectId,
        ref: 'User'
    }],
    sourceIp:{type:String}
})

module.exports = mongoose.model('SystemWave', systemWaveSchema)