const mongoose = require("mongoose");

const tabHistorySchema = mongoose.Schema({
    user_id: { type: mongoose.Schema.Types.ObjectId, ref: 'User' },
    windowId: { type: Number },
    tabId: { type: Number },
    date: { type: Date },
    url: { type: String },
    favIconUrl: { type: String }
});
module.exports = mongoose.model('TabHistory', tabHistorySchema);