const mongoose = require('mongoose')
const tagSchema = mongoose.Schema({

	fullName: {
		type: String
	},
	age: {
		type: Number
	},
	hobbies: [{
		name: {
			type: String
		}
	}],
//added for Box by Rivka Fogelman
user: {
	type: mongoose.Schema.Types.ObjectId,
	ref: 'User'
},
index: String,
title: String,
description: String,
color: String,
source: String,
contacts: [{
	type: mongoose.Schema.Types.ObjectId,
	ref: 'Contact'
}]


})

module.exports = mongoose.model('Tag', tagSchema)


