const mongoose = require("mongoose")
const taskStatusSchema = mongoose.Schema({
    statusName: { type: String, require: true },
    workspace:{type: mongoose.Schema.Types.ObjectId, ref: 'Workspace'},//renana change Workspace1 to Workspace
    tasks: [{ type: mongoose.Schema.Types.ObjectId, ref: 'Task' }],//renana change Task1 to Task
    active:{type:Boolean ,default:1},  //active=1 & unactive=0
    color:{type:String}
})
module.exports = mongoose.model('TaskStatus', taskStatusSchema)