const mongoose = require('mongoose')

const waveSchema = mongoose.Schema({

	timestamp: { type: Date, default: Date.now },
	//type: String,
	body: String,
	accepted: Boolean,
	files: [String],
	conversation: {
		type: mongoose.Schema.Types.ObjectId,
		ref: 'Conversation'
	},
	from: String,
	to: [String],
	readed: [Boolean],
    Ticket: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'Ticket'
    }
})

module.exports = mongoose.model('Wave', waveSchema)