//webhook schema - Adina Schustal
const mongoose = require('mongoose');

const WebhookSchema = mongoose.Schema({
    source: {
        type: String
    },
    blocked: {
        type: Boolean,
        default: false
    },
    properties:[],
    IP:{
        type:String
    },
    nickname:{
        type:String,
    },
    trashed:{
        type: Boolean,
        default: false
    }
});

module.exports = mongoose.model('Webhook', WebhookSchema);